import React, {Component} from 'react';
import {Tabs,Tab, Grid, Cell, Card, cardText, CardActions, CardTitle, Button, IconButton, CardMenu} from "react-mdl";

class Project extends Component {
    constructor(props) {
        super(props);
        this.state = {activeTab: 0 };
    }

    toggleCategories (){
       if (this.state.activeTab === 0 ){
            return(
                <div><h1>This is PERSONAL</h1></div>
            )
        } else if(this.state.activeTab === 1) {
            return(
                <div><h1>This is GROUP</h1></div>
            )
        } else if(this.state.activeTab === 1) {
        return(
            <div><h1>This is FUTURE DEVELOPMENTS</h1></div>
        )
    }
    } 

render () {
    return (
<div className="category-tabs">
    <Tabs activeTab= {this.state.activeTab} onChange = {(tabId)=>this.setState({ activeTab: tabId})} ripple>

<Tab><Card shadow={0} style={{width: '320px', height: '320px', margin: 'auto'}}>
    <CardTitle expand style={{color: '#fff', background: 'url(http://www.getmdl.io/assets/demos/dog.png) bottom right 15% no-repeat #46B6AC'}}>Update</CardTitle>
    <CardText>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit.
        Aenan convallis.
    </CardText>
    <CardActions border>
        <Button colored>View Updates</Button>
    </CardActions>
</Card></Tab>


<Tab>Group</Tab>


<Tab>Future</Tab>
</Tabs>

<section className="projects-grid">
    <Grid className="projects-grid">

        <Cell col={12}>
<div className="content"> {this.toggleCategories()}</div>
        </Cell>
    </Grid>

</section>
</div>

    )
}

    }


export default Project;
